mapper.update:
UPDATE pension
SET minprice=#{minprice}
WHERE no=#{no}

mapper.select:
  	 SELECT p.*,
			pr.*
	 FROM pension p 
	 JOIN pensionroom pr  ON pr.pcode = p.pcode and pr.no = p.no
  	WHERE p.pdetailregion = #{selectedText}

	  	 AND p.ptype = #{ptype}


	    AND 


       		 pr.pcomfort LIKE CONCAT('%'|| #{comfort}, '%')


        AND pr.ppeople >= #{ppeople}
